def play_tetris_song():
    for note, duration in tetris_song:
        if note != 'R':
            pwm.freq(int(note/2))
            pwm.duty(30)
        else:
            pwm.duty(0)

        time.sleep(duration)
        pwm.duty(0)
        time.sleep(0.01)
